\u1edbn l\\u00ean c\\u1ee7a c\\u00e2y, c\\u00f2n \\u0111au kh\\u1ed5 l\\u00e0 r\\u1ec5 c\\u00e2y kh\\u00f4ng ng\\u1eebng c\\u1eafm s\\u00e2u xu\\u1ed1ng l\\u00f2ng \\u0111\\u1ea5t. Nh\\u01b0ng b\\u1ea1n bi\\u1ebft \\u0111\\u1ea5y, kh\\u00f4ng c\\u00f3 r\\u1ec5 th\\u00ec c\\u00e2y kh\\u00f4ng th\\u1ec3 s\\u1ed1ng. M\\u1ecdi th\\u1ee9 \\u0111\\u1ec1u c\\u00f3 \\u00fd ngh\\u0129a c\\u1ee7a n\\u00f3. \\u0110\\u00f3 ch\\u00ednh l\\u00e0 s\\u1ef1 c\\u00e2n b\\u1eb1ng\\u201d \\u2013 Osho.\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-12-01 08:14:59\"\n    },\n    \"colibri-wp::header_front_page.button_group.style.textAlign\": {\n        \"value\": \"flex-start\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-12-01 08:15:10\"\n    },\n    \"colibri-wp::header_front_page.button_group.value\": {\n        \"value\": \"%5B%7B%22label%22%3A%22Like%20page%22%2C%22url%22%3A%22%23%22%2C%22button_type%22%3A%221%22%2C%22index%22%3A1%7D%5D\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-12-01 08:16:59\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', 'f2ea2019-354f-4afa-966c-4ee59ec96dd3', '', '', '2023-12-01 08:17:34', '2023-12-01 08:17:34', '', 0, 'http://localhost/wordpress/?p=59', 0, 'customize_changeset', '', 0),
